var theButton = document.getElementById("WAButton")
var pressed = false
theButton.addEventListener("click", async function () {
    if (pressed) {
        theButton.classList.remove("WAButtonActive")
        theButton.innerHTML = '<a id="whatIcon"><i class="bi bi-whatsapp"></i></a>'
        pressed =false
    } else {
        theButton.classList.add("WAButtonActive")
        await sleep(300)
        theButton.innerHTML = '<div onclick="copyNumber()" id="wpNumber"><span class="tooltiptext" id="myTooltip">Copiar</span>'+num+'</div><a href="https://api.whatsapp.com/send?phone=59899669085&text="><button id="whatBut" class="mt-1">Enviar Mensaje</button></a> '
        pressed =true
    }

});
